        var reader = command.ExecuteReader();
        while (reader.Read())
        {
            answers.Add(reader.GetAnswewes());
        }
        reader.Close();
        return answers;
    }
    public int number(int number,string userid)
    {
        var command = _connection.CreateCommand();
        int son;
        command.CommandText = $"SELECT tisket{number} FROM answeres WHERE userid = @userid";
        command.Parameters.AddWithValue("userid", userid);
        
        var reader = command.ExecuteReader();

        
        
        while (reader.Read())
        {
            son = reader.GetInt32(0);

            reader.Close();
            return son;
        }
        reader.Close();
        return 10;
    }
